function checkDates(current, fromDate, toDate) {

    //take msg date
    var fileDateYMD = current.split("_")[1];
    var fileYear = parseInt(fileDateYMD.split("-")[0]);
    var fileMonth = parseInt(fileDateYMD.split("-")[1]);
    var fileDay = parseInt(fileDateYMD.split("-")[2]);

    //take calendar dates
    var calendar_first = flow.get(fromDate) || 0;
    var minYear = parseInt(calendar_first.split('-')[0]);
    var minMonth = parseInt(calendar_first.split('-')[1]);
    var minDay = parseInt(calendar_first.split('-')[2]);

    var calendar_second = flow.get(toDate) || 0;
    var maxYear = parseInt(calendar_second.split('-')[0]);
    var maxMonth = parseInt(calendar_second.split('-')[1]);
    var maxDay = parseInt(calendar_second.split('-')[2]);

    var year_min = minYear < fileYear;
    var year_min_same = minYear == fileYear;
    var month_min = minMonth < fileMonth;
    var month_min_same = minMonth == fileMonth;
    var day_min = minDay <= fileDay;
    var min_check = year_min || (year_min_same && month_min) || (year_min_same && month_min_same && day_min);

    var year_max = fileYear < maxYear;
    var year_max_same = fileYear == maxYear;
    var month_max = fileMonth < maxMonth;
    var month_max_same = fileMonth == maxMonth;
    var day_max = fileDay <= maxDay;
    var max_check = year_max || (year_max_same && month_max) || (year_max_same && month_max_same && day_max);

    return min_check && max_check;
}

